mapper.update:
UPDATE t_oa_bank
SET deleted_flag = #{deletedFlag}
WHERE bank_id = #{bankId}

mapper.select:
SELECT *
FROM t_oa_bank
WHERE enterprise_id = #{enterpriseId}
AND account_number = #{accountNumber}
AND deleted_flag = #{deletedFlag}

    AND bank_id != #{excludeBankId}

SELECT
b.*,
e.enterprise_name AS enterpriseName
FROM t_oa_bank b
LEFT JOIN t_oa_enterprise e
ON b.enterprise_id = e.enterprise_id

    b.deleted_flag = #{queryForm.deletedFlag}

        AND (INSTR(b.bank_name, #{queryForm.keywords})
        OR INSTR(b.account_name, #{queryForm.keywords})
        OR INSTR(b.account_number, #{queryForm.keywords})
        OR INSTR(b.create_user_name, #{queryForm.keywords}))


        AND DATE_FORMAT(b.create_time, '%Y-%m-%d') >= #{queryForm.startTime}


        AND DATE_FORMAT(b.create_time, '%Y-%m-%d') <= #{queryForm.endTime}


        AND b.disabled_flag = #{queryForm.disabledFlag}


        AND b.enterprise_id = #{queryForm.enterpriseId}



    ORDER BY b.create_time DESC
