mapper.update:
UPDATE t_oa_invoice
SET deleted_flag = #{deletedFlag}
WHERE invoice_id = #{invoiceId}

mapper.select:
SELECT *
FROM t_oa_invoice
WHERE enterprise_id = #{enterpriseId}
AND account_number = #{accountNumber}
AND deleted_flag = #{deletedFlag}

    AND invoice_id != #{excludeInvoiceId}

SELECT
i.*,
e.enterprise_name AS enterpriseName
FROM t_oa_invoice i
LEFT JOIN t_oa_enterprise e
ON i.enterprise_id = e.enterprise_id

    i.deleted_flag = #{queryForm.deletedFlag}

        AND (INSTR(i.invoice_heads, #{queryForm.keywords})
        OR INSTR(i.account_number, #{queryForm.keywords})
        OR INSTR(i.create_user_name, #{queryForm.keywords}))


        AND DATE_FORMAT(i.create_time, '%Y-%m-%d') >= #{queryForm.startTime}


        AND DATE_FORMAT(i.create_time, '%Y-%m-%d') <= #{queryForm.endTime}


        AND i.disabled_flag = #{queryForm.disabledFlag}


        AND i.enterprise_id = #{queryForm.enterpriseId}



    ORDER BY i.create_time DESC
